_:n7552af34d15d472aa514778665e81d23b1 sh:message "Referenced features must be present within current graph closure for  topological feature." ;
    sh:select """
        prefix rdf:     <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
        PREFIX rdfs:    <http://www.w3.org/2000/01/rdf-schema#>
        prefix geojson: <https://purl.org/geojson/vocab#>

        SELECT $this (geojson:relatedFeatures as ?path) (?ref as ?value)
        WHERE {
         $this geojson:relatedFeatures/rdf:rest*/rdf:first ?ref .
         $this a ?geomtype .
         FILTER ( ?geomtype != geojson:Polygon)
         FILTER NOT EXISTS { ?ref ?p ?o }
        }
""" .

_:n7552af34d15d472aa514778665e81d23b2 sh:message "Lines topologies must reference points within current graph closure." ;
    sh:select """
        prefix rdf:     <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
        PREFIX rdfs:    <http://www.w3.org/2000/01/rdf-schema#>
        prefix geojson: <https://purl.org/geojson/vocab#>
        prefix dct:     <http://purl.org/dc/terms/>
        PREFIX geosparql: <http://www.opengis.net/ont/geosparql#>

        SELECT $this (geojson:topology as ?path) (?ref as ?value)
        WHERE {
         $this geojson:relatedFeatures/rdf:rest*/rdf:first ?ref .
         FILTER NOT EXISTS {
             ?ref (geojson:geometry|geosparql:hasGeometry|dct:spatial)/rdf:type geojson:Point }
        }
""" .

